        });

        &.media_image-container__wide-margin {
            margin-right: unit(@grid_gutter-width, px);

            .respond-to-max(599px {
                margin-left: 0;
                margin-right: 0;
            });
        }

    }

    &_image-container__right {
        display: block;

        .respond-to-min(600px {
            float: right;
            margin-left: unit(@grid_gutter-width / 2, px);
            margin-right: 0;
        });

        &.media_image-container__wide-margin {
            margin-left: unit(@grid_gutter-width, px);
            margin-right: 0;

            .respond-to-max(599px {
                margin-left: 0;
            });
        }

    }

    &_image {
        display: block;
    }

    &_body {
        .respond-to-max(599px {
            margin-top: unit(@grid_gutter-width / 2, px);
        });

        .respond-to-min(600px {
            display: table-cell;
            width: 10000px;
            vertical-align:top;
        });
    }

    .lt-ie8 &_body {
        zoom: 1;
        display: block;
        width: auto;
    }

    &__centered {
           .respond-to-max(599px {
               text-align: center;
           });

           .media_image {
               .respond-to-max(599px {
                   margin-left: auto;
                   margin-right: auto;
               });
           }
       }

}
